import { Component, OnInit, Output, EventEmitter } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { User } from '../../interface/user';
import { DataService } from '../../services/data.service';
import { FormValidator } from '../../shared/form/input/form-validator';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent implements OnInit {

  loading: boolean = false;
  registerError: boolean = false;
  @Output() success = new EventEmitter<boolean>(false);

  registerForm: FormGroup = this.fb.group({
    firstName: ['', [
      Validators.required,
      Validators.minLength(1)
    ]],
    lastName: ['', [
      Validators.required,
      Validators.minLength(1)
    ]],
    email: ['', [
      Validators.required,
      Validators.email
    ]],
    password: ['', [
      Validators.required,
      Validators.minLength(3)
    ]],
    age: ['', [
      Validators.required,
      Validators.min(18)
    ]]
  });

  constructor(private fb: FormBuilder, 
    private readonly formValidator: FormValidator,
    private dataService: DataService) { }

  ngOnInit(): void {
  }

  async register() {
    try {

      if (!this.registerForm.valid) {
        this.formValidator.validate(this.registerForm);
        return;
      }


      this.registerError = false;
      this.loading = true;
      const user: User = {... this.registerForm.value, token: null}
      this.dataService.insertUser = user;
      this.success.emit(true);
    } catch (error) {
      this.registerError = true;
    }
    this.loading = false;
  }

  get email() {
    return this.registerForm.get('email')
  }

  get password() {
    return this.registerForm.get('password')
  }

  get age() {
    return this.registerForm.get('age')
  }

}
<div class="wrapper">
  <h1 translate>REGISTER_WELCOME</h1>
  <form [formGroup]="registerForm" (ngSubmit)="register()">
    <!-- <app-input
      formControlName="firstName"
      [parentForm]="registerForm"
      [fieldName]="'firstName'"
      [label]="'NAME_FIRST' | translate"
    >
    </app-input> -->

    <app-generic-input
      formControlName="firstName"
      placeholder="person.firstName"
    >
    </app-generic-input>

    <app-generic-input formControlName="lastName" placeholder="person.lastName">
    </app-generic-input>

    <app-generic-input
      formControlName="email"
      placeholder="test@test.com"
      label="person.email"
      iconPrefix="email"
    >
    </app-generic-input>

    <app-generic-input
      formControlName="password"
      placeholder="person.password"
      type="password"
    >
    </app-generic-input>

    <!-- <app-input
      formControlName="lastName"
      [parentForm]="registerForm"
      [fieldName]="'lastName'"
      [label]="'NAME_LAST' | translate"
    >
    </app-input> -->
    <!-- <app-input
      formControlName="email"
      [parentForm]="registerForm"
      [fieldName]="'email'"
      [label]="'EMAIL' | translate"
    >
    </app-input> -->
    <!-- <app-input
      formControlName="password"
      [parentForm]="registerForm"
      [fieldName]="'password'"
      [label]="'PASSWORD' | translate"
      [type]="'password'"
    >
    </app-input> -->
    <!-- <app-input
      formControlName="age"
      [parentForm]="registerForm"
      [fieldName]="'age'"
      [label]="'DOB' | translate"
    >
    </app-input> -->
    <button mat-raised-button type="submit" [disabled]="loading">
      <span translate>REGISTER</span>
    </button>
  </form>
</div>
