import { Component, Input, OnInit } from '@angular/core';
import { RandomProduct } from 'src/app/interface/product';

@Component({
  selector: 'app-products-list',
  templateUrl: './products-list.component.html',
  styleUrls: ['./products-list.component.scss']
})
export class ProductsListComponent implements OnInit {

  @Input() products!: Promise<RandomProduct[]>

  constructor() { }

  ngOnInit(): void {
  }

}
<mat-list-option mat *ngFor="let product of products | async" [value]="product">
  <img matListAvatar [src]="product?.image" alt="..." />
  <h3 matLine class="flex">
    <span>{{ product.title }}</span>
    <span>{{ product.price }}$</span>
  </h3>
  <p matLine class="flex">
    <span> {{ product.category }}</span>
  </p>
</mat-list-option>
