import { Component, OnInit } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { RandomUser } from 'src/app/domain-layer/entities/random-users';
import { CrmFormsService } from 'src/app/services/crm-forms.service';

@Component({
  selector: 'app-search-input',
  templateUrl: './search-input.component.html',
  styleUrls: ['./search-input.component.scss']
})
export class SearchInputComponent implements OnInit {


  searchFormControl: FormControl = new FormControl('')
  filteredCustomers?: Promise<RandomUser[]>

  constructor() {  }

  ngOnInit(): void {
  }

}
<mat-form-field class="example-full-width">
  <input
    matInput
    placeholder="{{'Search customer' | translate}}"
    [matAutocomplete]="auto"
    [formControl]="searchFormControl"
  />

  <mat-autocomplete #auto="matAutocomplete">
    <mat-option
      *ngFor="let customer of filteredCustomers | async"
      [value]="customer.name"
    >
      <img
        class="option-img"
        aria-hidden
        [src]="customer?.picture?.thumbnail"
        height="25"
      />
      <span>{{ customer.name.first }} {{ customer.name.last }}</span>
      <small>Email: {{ customer.email }}</small>
    </mat-option>
  </mat-autocomplete>
</mat-form-field>
