import { Component, OnInit } from '@angular/core';
import { FormGroup, Validators, FormBuilder} from '@angular/forms'
import { AuthService } from '../../services/auth.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.scss']
})
export class LoginComponent implements OnInit {

  loading: boolean = false;
  success: boolean;
  loginError: boolean;

  loginForm: FormGroup = this.fb.group({
    email: ['', [
      Validators.required,
      Validators.email
    ]],
    password: ['', [
      Validators.required,
      Validators.minLength(3)
    ]]
  });

  constructor(private fb: FormBuilder, private authService: AuthService) {
    this.loginError = false;
    this.success = false; 
  }

  ngOnInit(): void {   
  }

  async login() {
    try {
      this.loginError = false;
      this.loading = true;
      await this.authService.login(this.loginForm.value)
      this.success = true
    }
    catch (error) {
      this.loginError = true;
    }
    this.loading = false;
  }

  get email() {
    return this.loginForm.get('email')
  }

  get password() {
    return this.loginForm.get('password')
  }

}
<div class="wrapper">
  <h1 translate>login-welcome</h1>
  <form [formGroup]="loginForm" (ngSubmit)="login()">
    <mat-form-field>
      <mat-label for="email" translate>email</mat-label>
      <input
        id="email"
        matInput
        type="text"
        placeholder="test@test.com"
        formControlName="email"
      />
    </mat-form-field>
    <mat-form-field>
      <mat-label for="pass" translate>password</mat-label>
      <input id="pass" matInput type="password" formControlName="password" />
    </mat-form-field>
    <button
      mat-raised-button
      type="submit"
      [disabled]="!loginForm.valid || loading"
      translate
    >
      {{ loading ? "loading" : !success ? "login" : "success" }}
    </button>
  </form>
  <div class="helpers">
    <mat-error class="worng-login" *ngIf="loginError">
      <span translate>login-error-try-this</span>
      <span>test@test.com:123456</span>
    </mat-error>
    <mat-error *ngIf="email?.touched && email?.invalid" translate>
      input-error-email
    </mat-error>
    <mat-error *ngIf="password?.touched && password?.invalid" translate>
      input-error-pass-min
    </mat-error>
  </div>
</div>
